import React, { useState } from "react";
import "./App.css";

function App() {
  const [isSidebarOpen, setSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="app-container">
      {isSidebarOpen && (
        <div className="sidebar">
          <button className="nav-button">Dashboard Overview</button>
          <button className="nav-button">Geospatial Map</button>
          <button className="nav-button">Charts & Graphs</button>
          <button className="nav-button">ISP Leaderboard</button>
          <button className="nav-button">History Analysis</button>
          <button className="toggle-sidebar-btn" onClick={toggleSidebar}>
            {isSidebarOpen ? 'Close Sidebar' : 'Open Sidebar'}
          </button>

        </div>
      )}
      <div className="main-content">
        <header className="header">



        </header>
        <div className="chat-area">
          {/* This is where your main content will be displayed */}
          <h1>Welcome to the Network Performance Platform</h1>
          <p>Select an option from the sidebar to get started.</p>
        </div>
      </div>
    </div>
  );
}

export default App;
