// src/Heatmap.js
import React, { useRef, useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat/dist/leaflet-heat.js';

const Heatmap = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    if (mapRef.current && !mapRef.current._leaflet_id) {
      // Initialize the map
      const map = L.map(mapRef.current, {
        center: [9.0820, 8.6753], // Center of Africa
        zoom: 3, // Adjust zoom level as needed
      });

      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      // Heatmap data (example)
      const heatData = [
        [9.0820, 8.6753, 0.5], // Lat, Lng, Intensity
        [10.7749, 7.4194, 0.7],
        [8.7949, 9.4294, 0.6],
        // Add more lat/lng/intensity points here
      ];

      // Add heatmap layer
      L.heatLayer(heatData, { radius: 25 }).addTo(map);

      // Set bounds for Africa (example bounds)
      const bounds = [
        [-35.0, -20.0], // Southwest coordinates
        [37.0, 50.0]    // Northeast coordinates
      ];
      map.fitBounds(bounds);

      // Cleanup function
      return () => {
        map.remove();
      };
    }
  }, []);

  // Ensure map container has dimensions
  return <div ref={mapRef} style={{ height: '500px', width: '100%', position: 'relative' }} />;
};

export default Heatmap;
