// src/Heatmap.js
import React, { useRef, useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat/dist/leaflet-heat.js';

const Heatmap = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    // Create map only if it doesn't exist
    if (mapRef.current && !mapRef.current._leaflet_id) {
      const map = L.map(mapRef.current).setView([37.7749, -122.4194], 10);

      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      // Heatmap data
      const heatData = [
        [37.7749, -122.4194, 0.5], // Lat, Lng, Intensity
        [37.7849, -122.4094, 0.7],
        [37.7949, -122.4294, 0.6],
        // Add more lat/lng/intensity points here
      ];

      // Add heatmap layer
      L.heatLayer(heatData, { radius: 25 }).addTo(map);

      // Cleanup function to remove map instance on component unmount
      return () => {
        map.remove();
        mapRef.current = null; // Clear mapRef
      };
    }
  }, []);

  return <div ref={mapRef} style={{ height: '500px', width: '100%' }} />;
};

export default Heatmap;
