import React, { useEffect, useState } from 'react';
import MetricCard from './MetricCard';
import FilterBar from './FilterBar';
import '../Styling/DashboardOverview.css';
import '../Styling/FilterBar.css';

const DashboardOverview = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('/api/isp-data/')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div className="dashboard-overview">
      <h1>Historical Dashboard Overview</h1>
      <FilterBar />
      <div className="card-grid">
        {data ? (
          <>
            <MetricCard title="Throughput" value={data.throughput + ' Mbps'} />
            <MetricCard title="Min RTT" value={data.min_rtt + ' ms'} />
            <MetricCard title="Packet Loss" value={data.packet_loss + '%'} />
            <MetricCard title="Country" value={data.country} />
            <MetricCard title="City" value={data.city || 'N/A'} />
            <MetricCard title="ISP" value={data.isp} />
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default DashboardOverview;
