import React, { useEffect, useState } from 'react';
import MetricCard from './MetricCard';
import FilterBar from './FilterBar';
import '../Styling/DashboardOverview.css';
import '../Styling/FilterBar.css';

const DashboardOverview = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('/api/isp-data/')  // Make sure this matches your actual API endpoint
      .then(response => response.json())
      .then(data => setData(data))  // Store the API data in the state
      .then(data => {
        console.log('Fetched this data: ',data);  // Log the data to inspect its structure
        setData(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Check if data is available before rendering the MetricCards
  if (!data) {
    return <div>Loading...</div>;  // Add a loading indicator until data is fetched
  }

  return (
    <div className="dashboard-overview">
      <h1>Historical Dashboard Overview</h1>
      <FilterBar />
      <div className="card-grid">
        <MetricCard title="Average Latency" value={`${data.average_latency || 'N/A'} ms`} />
        <MetricCard title="Download Speed" value={`${data.download_speed || 'N/A'} Mbps`} />
        <MetricCard title="Upload Speed" value={`${data.upload_speed || 'N/A'} Mbps`} />
        <MetricCard title="Packet Loss" value={`${data.packet_loss || 'N/A'}%`} />
        <MetricCard title="Top 3 ISPs" value={Array.isArray(data.top_isps) ? data.top_isps.join(', ') : 'N/A'} />
        <MetricCard title="Highest Latency ISP" value={data.highest_latency_isp || 'N/A'} />
      </div>
    </div>
  );
};

export default DashboardOverview;
